import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { convertTypeAcquisitionFromJson } from 'typescript';

function CreateBunker(props){
    let bunkerName = ""
        if (!props.isCreatingBunker) {
            bunkerName = "Editing"
        } else {
            bunkerName = "Creating"
        }

    const [rooms, setRooms] = useState([]);
    const [newName, setNewName] = useState("");
    const [newNorth, setNewNorth] = useState("");
    const [newSouth, setNewSouth] = useState("");
    const [newEast, setNewEast] = useState("");
    const [newWest, setNewWest] = useState("");
    const [newDescription, setNewDescription] = useState("");

    function handleAddRoom (e) {
        //e.preventDefault();
        const newRoom = {
            name: newName,
            north: newNorth,
            south: newSouth,
            east: newEast,
            west: newWest,
            description: newDescription
        };

        setRooms([...rooms, newRoom]);
        setNewName("");
        setNewNorth("");
        setNewSouth("");
        setNewEast("");
        setNewWest("");
        setNewDescription("");
    }

    function handleRemoveRoom (index) {
        const newRooms = [...rooms];
        newRooms.splice(index, 1);
        setRooms(newRooms);
    }

    return (
        <div>
            <form onSubmit={handleAddRoom}>
                <input
                    type="text"
                    placeholder="Name"
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="North"
                    value={newNorth}
                    onChange={(e) => setNewNorth(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="South"
                    value={newSouth}
                    onChange={(e) => setNewSouth(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="East"
                    value={newEast}
                    onChange={(e) => setNewEast(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="West"
                    value={newWest}
                    onChange={(e) => setNewWest(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={newDescription}
                    onChange={(e) => setNewDescription(e.target.value)}
                />
                <button type="submit">Add Room</button>
            </form>
            {rooms.map((room, index) => (
                <div key={index} className = "roomCards">
                    <h3>{room.name}</h3>
                    <p>{room.north}</p>
                    <p>{room.south}</p>
                    <p>{room.east}</p>
                    <p>{room.west}</p>
                    <p>{room.description}</p>
                    <button onClick={() => handleRemoveRoom(index)}>Delete</button>
                </div>
            ))}
        </div>
    );

}









