import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

function CreateBunker(props){
    const [bunkerBool, setBunkerBool] = useState(props.isCreatingBunker);
    const [bunkerSaved, setBunkerSaved] = useState(false);
    const [bunkerName, setNewBunkerName] = useState("");
    const [rooms, setRooms] = useState([]);
    const [newName, setNewName] = useState("");
    const [newNorth, setNewNorth] = useState("");
    const [newSouth, setNewSouth] = useState("");
    const [newEast, setNewEast] = useState("");
    const [newWest, setNewWest] = useState("");
    const [newDescription, setNewDescription] = useState("");

    function handleAddRoom (e) {
        e.preventDefault();
        const newRoom = {
            name: newName,
            north: newNorth,
            south: newSouth,
            east: newEast,
            west: newWest,
            description: newDescription
        };

        setRooms([...rooms, newRoom]);
        setNewName("");
        setNewNorth("");
        setNewSouth("");
        setNewEast("");
        setNewWest("");
        setNewDescription("");
    }

    function handleRemoveRoom (index) {
        const newRooms = [...rooms];
        newRooms.splice(index, 1);
        setRooms(newRooms);
    }

    function handleBunkerSave() {
        //Save bunker to local storage here
        console.log("Saving bunker to local storage")
        console.log(rooms)
        localStorage.setItem("bunker" + bunkerName, JSON.stringify(rooms))
        setBunkerBool(false)
        setBunkerSaved(true)
    }

    function loadBunker(bunkerName) {
        console.log('bunkerName', bunkerName, typeof bunkerName)
        let string = ''

        const bunkerString = localStorage.getItem(bunkerName)    
        console.log(bunkerString)
        const bunker = JSON.parse(bunkerString);
        console.log(bunker)
        if (bunker) setRooms(bunker)
    }

    const allSavedBunkers = Object.keys(localStorage)

    return (
        <div>
            <h1>{bunkerBool ? 'Creating' : bunkerSaved ? 'Editing - Saved' : 'Editing'}</h1>
            <select>
                {allSavedBunkers.map((bunker, index) => <option key={index} onClick={() => loadBunker(bunker)}>{bunker}</option>)}
            </select>
            <button className="bunkerSave" onClick={loadBunker}>Load</button>
            <Link to="/manage">Back</Link>
            <button className = "bunkerSave" onClick={() => handleBunkerSave()}>Save</button>
            <input
                type="text"
                placeholder="Bunker Name"
                value={bunkerName}
                onChange={(e) => setNewBunkerName(e.target.value)}
            />
            <form onSubmit={handleAddRoom}>
                <input
                    type="text"
                    placeholder="Name"
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="North"
                    value={newNorth}
                    onChange={(e) => setNewNorth(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="South"
                    value={newSouth}
                    onChange={(e) => setNewSouth(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="East"
                    value={newEast}
                    onChange={(e) => setNewEast(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="West"
                    value={newWest}
                    onChange={(e) => setNewWest(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={newDescription}
                    onChange={(e) => setNewDescription(e.target.value)}
                />
                <button type="submit">Add Room</button>
            </form>
            {rooms.map((room, index) => (
                <div key={index} className = "roomCards">
                    <h3>{room.name}</h3>
                    <p>{room.north}</p>
                    <p>{room.south}</p>
                    <p>{room.east}</p>
                    <p>{room.west}</p>
                    <p>{room.description}</p>
                    <button onClick={() => handleRemoveRoom(index)}>Delete</button>
                </div>
            ))}
        </div>
    );

}


