import React, {useState, useEffect } from 'react';
import './App.css';
import GameManager from './GameManager';

const BunkerManager = () => {
    const [loadFile, setLoad] = useState(false)
    const [createBunker, setCreate] = useState(false)
    const [editBunker, setEdit] = useState(false)
    const [bunkerList, setBunkerList] = useState(false)
    const [back, setBack] = useState(false)

    function loadBunker() {
        if(loadFile) {
            return (
                <div>
                    <input type = "file" accept = ".json" className = "bunkerEditorLoadInput"/>
                    <button onClick = {() => {setLoad(false)}} className = "bunkerEditorLoadButton">
                        Load Bunker
                    </button>
                </div>
            )
        }
        else if (createBunker) {
            return (
                <div>
                    <input type = "text" className = "bunkerEditorCreateInput"/>
                    <button onClick = {() => {setCreate(false)}} className = "bunkerEditorCreateButton">
                        Create Bunker
                    </button>
                </div>
            )
        }
        else if (editBunker) {
            return (
                <div>
                    <input type = "text" className = "bunkerEditorEditInput"/>
                    <button onClick = {() => {setEdit(false)}} className = "bunkerEditorEditButton">
                        Edit Bunker
                    </button>
                </div>
            )
        }
        else if (bunkerList) {
            return (
                <div>
                    <button onClick = {() => {setBunkerList(false)}} className = "bunkerEditorListButton">
                        Bunker List
                    </button>
                </div>
            )
        }
        else if(back) {
            return (
                <div>
                    <button onClick = {() => {setBack(false)}} className = "bunkerEditorBackButton">
                        Back
                    </button>
                </div>
            )
        }
        
    }



    return (
        <div className = "BunkerEditor">
            {
                !back ?
                <div>
                    <button onClick = {() => {setLoad(true)}} className = "bunkerEditorLoadButton">
                        Load Bunker
                    </button>
                    <button onClick = {() => {setCreate(true)}} className = "bunkerEditorCreateButton">
                        Create Bunker
                    </button>
                    <button onClick = {() => {setEdit(true)}} className = "bunkerEditorEditButton">
                        Edit Bunker
                    </button>
                    <button onClick = {() => {setBunkerList(true)}} className = "bunkerEditorListButton">
                        Bunker List
                    </button>
                    <button onClick = {() => {setBack(true)}} className = "bunkerEditorBackButton">
                        Back
                    </button>
                </div>
                : <GameManager/>
            }
        </div>
    );
}

export default BunkerManager;
