import React, {useState, useEffect } from 'react';
import './App.css';
import data from './Test.json'

const GameManager = () => {
  const [roomName, setRoomName] = useState("Room1")
  const [command, setCommand] = useState("")

  function doCommand() {
    const room = data[roomName]
    // Check for valid command
    if (room[command] && room[command] != "NA" && room[command] != "N") {
      console.log("valid command")
      console.log(command)
      console.log(command)
      // Check movements
      if (["East", "West", "North", "South"].includes(command)) {
        setRoomName(room[command].Name)
      }
    }
  }

  return (
    <div>
      <p>{roomName}</p>
      <input onInput={setCommand} />
      <button onClick={doCommand}>Submit</button>
    </div>
  )
}

export default GameManager;
