import { useState } from "react";
import { Link } from "react-router-dom";
import { Button, Card } from "@mui/material";

const BunkerChoice = (bunkerList) => {

    function BunkerCard(bunker, index) {
        return (
            <Card sx={{ minWidth: 275 }}>
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {bunker.name}
                    </Typography>
                    <Typography variant="body2">
                        Room Count: {bunker.rooms.length}
                    </Typography>
                    <Typography variant="body2">
                        Description: {bunker.description}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button size="small" variant="outlined" onClick={() => { handleChoice(bunker) }}>Choose</Button>
                </CardActions>
            </Card>
        );
    }


    return (
        <div>
            <Link to="/">
                <Button variant='contained'>Back</Button>
            </Link>
            {bunkerList.map((bunker, index) => (
                <div key={index}>
                    {BunkerCard(bunker, index)}
                </div>
            ))}
        </div>
    );

}
export default BunkerChoice;
